import React, { useState } from "react";
function useInput(defaultValue) {
  const [value, setValue] = useState(defaultValue);
  function onChange(e) {
    setValue(e.target.value);
  }
  return {
    value,
    onChange,
  };
}
function App() {
  return (
    <div>
      <input placeholder="Type in here" />
      <span>Value: {inputProps.value} </span>
    </div>
  );
}
export default App;
