import { useState, useEffect, useRef } from "react";

function App() {
  const [inputValue, setInputValue] = useState();
  const count = useRef(0);

  useEffect(() => {
    count.current = count.current + 1;
  });

  return (
    <>
      <input
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
      />
      <h1>Count: {count.current}</h1>
    </>
  );
}
export default App;
