import { useState, useEffect, useRef, Component } from "react";
class App extends Component {
  state = {
    items: [],
    text: "",
  };
  handleChange(e) {
    this.setState({ text: e.target.value });
  }
  render() {
    return (
      <>
        <div>TOPIC</div>
        <input value={this.state.text} onChange={this.handleChange} placeholder="write something" />
      </>
    );
  }
}
export default App;
