import React, { useState } from "react";
import './style.css'
function useInput(inp) {
  const [value, setValue] = useState(inp);
  function onChange(e) {
    setValue(e.target.value);
  }
  return {
    value,
    onChange,
  };
}
function App() {
  const inputProps = useInput();
  return (
    <div className="wow">
      <input {...inputProps}/>
      <div>Value: {inputProps.value} </div>
    </div>
  );
}
export default App;
