import React, { useState } from "react";
import './style.css'
function useInput(inp) {
  const [value, setValue] = useState(inp);
  function onChange(e) {
    setValue(e.target.value);
  }
  return {
    value,
    onChange,
  };
}
function App() {
  const inputProps = useInput();
  return (
    <div className="wow">
      <input {...inputProps}/>
      <div>Value: {inputProps.value} </div>
      <Welcome name="Sara" age="12" />
      <Welcome name="Cahal" />
      <Welcome name="Edite" />
    </div>
  );
}
function Welcome(yty) {
  return <h1>Hello, {yty.name},{yty.age}</h1>;
}

export default App;
